INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('41', 'positivo', 'Un misterio clásico de Sherlock Holmes. Intriga y lógica en cada página.', '41', '41');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('42', 'positivo', 'Un gran caso de Holmes con una trama bien construida. Muy entretenido.', '42', '42');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('43', 'positivo', 'Una de las mejores historias de Sherlock Holmes. Intriga y suspense.', '43', '43');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('44', 'neutral', 'Una buena historia, pero no tan fuerte como otros casos de Holmes.', '44', '44');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('45', 'negativo', 'El misterio no es tan intrigante como otros casos de Holmes.', '45', '45');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('46', 'negativo', 'El ritmo es lento y la trama se siente un poco predecible.', '46', '46');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('47', 'negativo', 'La resolución del misterio fue decepcionante y no tan impactante como esperaba.', '47', '47');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('48', 'negativo', 'No me gustó tanto como otras historias de Sherlock Holmes.', '48', '48');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('49', 'positivo', 'Un clásico de la ciencia ficción con una trama muy original y bien desarrollada.', '49', '49');
INSERT INTO comentario (id, tipo, contenido, usuario_id, libro_id) VALUES ('50', 'positivo', 'Una continuación que expande la trama de manera efectiva. Muy bien escrito.', '50', '50');
